macro.sp_dbt_poc.test_column_is_integer.macro_sql:
{% test column_is_integer(model, column_name) %}

with validation as (
    select 
        {{column_name}} as int_field
    from {{model}}
),

validation_error as (
    select 
        int_field
    from validation
    where is_integer(int_field) != 0
)

select 
    *
from validation_error

{% endtest %}

