macro.sp_dbt_poc.test_check_distinct_min.macro_sql:
{% test check_distinct_min_max_counts(model, column_name, min, max) %}
    WITH get_metrics AS (
        SELECT COUNT( DISTINCT {{column_name}})  AS dist_count_rank
                , MIN({{column_name}})    AS min_rank
                , MAX({{column_name}})    AS max_rank
        FROM {{ model }}
    ) 

    SELECT * 
    FROM get_metrics
    WHERE dist_count_rank > {{max}}
        AND min_rank < {{min}}
        AND max_rank > {{max}}

{% endtest %}

